import React from 'react';
import { TextField, IconButton, Box } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatBox = ({ sendMessage }) => {
  const [message, setMessage] = React.useState('');
  const [chatMessages, setChatMessages] = React.useState([]);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSendMessage = () => {
    if (message.trim() !== '') {
      setChatMessages([
        ...chatMessages,
        { text: message, sender: 'user', id: chatMessages.length },
      ]);
      sendMessage(message);
      setMessage('');
    }
  };

  // Function to render chat bubbles
  const renderChatBubbles = () => {
    return chatMessages.map((msg) => {
      const isUser = msg.sender === 'user';
      const bubbleColor = isUser ? 'blue' : 'green';

      return (
        <Box
          key={msg.id}
          sx={{
            display: 'flex',
            justifyContent: isUser ? 'flex-end' : 'flex-start',
            marginBottom: '10px',
          }}
        >
          <Box
            sx={{
              backgroundColor: bubbleColor,
              color: 'white',
              borderRadius: '10px',
              padding: '8px 12px',
              maxWidth: '70%',
            }}
          >
            {msg.text}
          </Box>
        </Box>
      );
    });
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: '20px', // Adjust position from bottom as needed
        width: '50%', // Adjust width of the chatbox
        left: '350px', // Adjust the left position if needed
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {/* Render chat bubbles */}
        {renderChatBubbles()}
      </Box>
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          borderRadius: '10px',
          backgroundColor: 'black', // Change text box background color here
          color: 'white', // Set text color to white
          border: '2px solid #ccc', // Grey border
        }}
      >
        <TextField
          placeholder="  Type your message"
          variant="standard"
          size="small"
          fullWidth
          sx={{
            borderRadius: '10px',
            flexGrow: 1,
            backgroundColor: 'transparent', // Set transparent background color
            '& input': {
              color: 'white', // Set input text color to white
            },
            '& .MuiInputBase-input': {
              caretColor: 'white', // Set caret color to white
            },
            '& label': {
              color: 'white !important', // Set label color to white
            },
          }}
          value={message}
          onChange={handleMessageChange}
        />
        <IconButton
          color="primary"
          aria-label="send message"
          onClick={handleSendMessage}
          sx={{ marginLeft: '5px' }} // Adjust margin as needed
        >
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBox;
