import React, { useState } from 'react';
import { TextField, IconButton, Box } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatBox = () => {
  const [message, setMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSendMessage = async () => {
    if (message.trim() !== '') {
      const newMessage = { text: message, sender: 'user', id: chatMessages.length };

      // Update the UI optimistically before sending the message to the server
      setChatMessages([...chatMessages, newMessage]);
      setMessage('');

      try {
        const response = await fetch('http://localhost:5000/send-message', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ message: message }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok.');
        }

        const data = await response.json();

        const serverResponse = {
          text: data.message,
          sender: 'chatbot',
          id: chatMessages.length + 1,
        };

        setChatMessages([...chatMessages, serverResponse]);
      } catch (error) {
        console.error('Error sending/receiving message:', error);
      }
    }
  };

  const renderChatBubbles = () => {
    return chatMessages.map((msg) => {
      const isUser = msg.sender === 'user';
      const bubbleColor = isUser ? 'blue' : 'green';

      return (
        <Box
          key={msg.id}
          sx={{
            display: 'flex',
            justifyContent: isUser ? 'flex-end' : 'flex-start',
            marginBottom: '10px',
          }}
        >
          <Box
            sx={{
              backgroundColor: bubbleColor,
              color: 'white',
              borderRadius: '10px',
              padding: '8px 12px',
              maxWidth: '70%',
            }}
          >
            {msg.text}
          </Box>
        </Box>
      );
    });
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: '20px', // Adjust position from bottom as needed
        width: '50%', // Adjust width of the chatbox
        left: '350px', // Adjust the left position if needed
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {/* Render chat bubbles */}
        {renderChatBubbles()}
      </Box>
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          borderRadius: '10px',
          backgroundColor: 'black', // Change text box background color here
          color: 'white', // Set text color to white
          border: '2px solid #ccc', // Grey border
        }}
      >
        <TextField
          placeholder="  Type your message"
          variant="standard"
          size="small"
          fullWidth
          value={message}
          onChange={handleMessageChange}
        />
        <IconButton
          color="primary"
          aria-label="send message"
          onClick={handleSendMessage}
        >
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBox;
