import React from 'react';
import { TextField, IconButton, Box } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatBox = ({ sendMessage }) => {
  const [message, setMessage] = React.useState('');

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSendMessage = () => {
    if (message.trim() !== '') {
      sendMessage(message);
      setMessage('');
    }
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: '20px', // Adjust position from bottom as needed
        width: '80%', // Adjust width of the chatbox
        margin: '0 auto', // Center the chatbox horizontally
        left:'150px'
      }}
    >
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          borderRadius: '10px',
          backgroundColor: 'grey', // Change text box background color here
          color: 'white', // Set text color to white
        }}
      >
        <TextField
          placeholder="Type your message"
          variant="standard"
          size="small"
          fullWidth
          sx={{ borderRadius: '10px', flexGrow: 1 }}
          value={message}
          onChange={handleMessageChange}
        />
        <IconButton
          color="primary"
          aria-label="send message"
          onClick={handleSendMessage}
        >
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBox;
