import React, { useEffect } from 'react';
import { TextField, IconButton, Box } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatBox = ({ sendMessage }) => {
  const [message, setMessage] = React.useState('');
  const [userMessages, setUserMessages] = React.useState([]);
  const [serverMessages, setServerMessages] = React.useState([]);

  useEffect(() => {
    // Simulate initial message from the chatbot
    const initialChatbotMessage = { text: 'Hi', sender: 'chatbot', id: 0 };
    setServerMessages([initialChatbotMessage]);
  }, []);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSendMessage = async () => {
    if (message.trim() !== '') {
      const newMessage = { text: message, sender: 'user', id: userMessages.length };

      // Update the UI optimistically before sending the message to the server
      setUserMessages([...userMessages, newMessage]);
      setMessage('');

      try {
        // Simulating sending a message to the server...
        // Replace this with your actual server request
        // const response = await fetch('http://localhost:5000/send-message', {
        //   method: 'POST',
        //   headers: {
        //     'Content-Type': 'application/json',
        //   },
        //   body: JSON.stringify({ message: message }),
        // });

        // Simulated server response data
        const data = { message: 'Hello from the server!' };

        const serverResponse = {
          text: data.message,
          sender: 'chatbot',
          id: serverMessages.length + 1,
        };

        setServerMessages([...serverMessages, serverResponse]);
      } catch (error) {
        console.error('Error sending/receiving message:', error);
      }
    }
  };

  const renderChatBubbles = (messages) => {
    return messages.map((msg) => {
      const isUser = msg.sender === 'user';
      const bubbleColor = isUser ? 'blue' : 'green';

      return (
        <Box
          key={msg.id}
          sx={{
            display: 'flex',
            justifyContent: isUser ? 'flex-end' : 'flex-start',
            marginBottom: '10px',
          }}
        >
          <Box
            sx={{
              backgroundColor: bubbleColor,
              color: 'white',
              borderRadius: '10px',
              padding: '8px 12px',
              maxWidth: '70%',
            }}
          >
            {msg.text}
          </Box>
        </Box>
      );
    });
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: '20px', // Adjust position from bottom as needed
        width: '50%', // Adjust width of the chatbox
        left: '350px', // Adjust the left position if needed
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {/* Render user messages */}
        {renderChatBubbles(userMessages)}

        {/* Render server messages */}
        {renderChatBubbles(serverMessages)}
      </Box>
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          borderRadius: '10px',
          backgroundColor: 'black', // Change text box background color here
          color: 'white', // Set text color to white
          border: '2px solid #ccc', // Grey border
        }}
      >
        <TextField
          placeholder="  Type your message"
          variant="standard"
          size="small"
          fullWidth
          sx={{
            borderRadius: '10px',
            flexGrow: 1,
            backgroundColor: 'transparent', // Set transparent background color
            '& input': {
              color: 'white', // Set input text color to white
            },
            '& .MuiInputBase-input': {
              caretColor: 'white', // Set caret color to white
            },
            '& label': {
              color: 'white !important', // Set label color to white
            },
          }}
          value={message}
          onChange={handleMessageChange}
        />
        <IconButton
          color="primary"
          aria-label="send message"
          onClick={handleSendMessage}
          sx={{ marginLeft: '5px' }} // Adjust margin as needed
        >
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBox;
