import React from 'react';
import { Box, Typography } from '@mui/material';

const MessageBubble = ({ message, sender }) => {
  const isUser = sender === 'user';
  const bubbleColor = isUser ? '#2979ff' : '#00c853'; // Blue and green shades suitable for black background
  const textColor = isUser ? 'white' : 'white'; // Adjust text color for better visibility

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: isUser ? 'flex-end' : 'flex-start',
        mb: 1,
      }}
    >
      <Box
        sx={{
          backgroundColor: bubbleColor,
          color: textColor,
          borderRadius: '10px',
          padding: '8px 12px',
          maxWidth: '70%',
        }}
      >
        <Typography variant="body2">{message}</Typography>
      </Box>
    </Box>
  );
};

export default MessageBubble;
